package matrix.workingPeoplesImitation.task_30_Substring_with_Concatenation_of_All_Words;

import org.junit.Test;

import java.util.List;

import static org.junit.Assert.assertEquals;

public class Tests {
    private final Solution testingClass = new Solution();

    @Test
    public void checkTestcase01() {
        String s = "barfoothefoobarman";
        String[] words = {"foo", "bar"};
        List<Integer> expected = List.of(0, 9);
        assertEquals(expected, testingClass.findSubstring(s, words));
    }

    @Test
    public void checkTestcase02() {
        String s = "wordgoodgoodgoodbestword";
        String[] words = {"word", "good", "best", "word"};
        List<Integer> expected = List.of();
        assertEquals(expected, testingClass.findSubstring(s, words));
    }

    @Test
    public void checkTestcase03() {
        String s = "barfoofoobarthefoobarman";
        String[] words = {"bar", "foo", "the"};
        List<Integer> expected = List.of(6, 9, 12);
        assertEquals(expected, testingClass.findSubstring(s, words));
    }

    @Test
    public void checkTestcase04() {
        String s = "";
        String[] words = {"dhvf","sind","ffsl","yekr","zwzq","kpeo","cila","tfty","modg","ztjg","ybty","heqg","cpwo","gdcj","lnle","sefg","vimw","bxcb"};
        List<Integer> expected = List.of(935);
        assertEquals(expected, testingClass.findSubstring(s, words));
    }
}
